property.code:
(function(){
  document.observe('dom:loaded', function () {
    if($('version')) {
      Event.observe($('version'), "focus", function() {
        new XWiki.widgets.Suggest(this, {
             script: '$xwiki.getURL("${doc.space}.WebHome", "view")?xpage=suggest&amp;classname=ReleaseNotes.Code.ReleaseNoteClass&amp;fieldname=version&amp;secCol=-&amp;',
             varname: "input", 
             seps: " ,|",
             offsety: 13
        });
      });
    }
    if($('product')) {
      Event.observe($('product'), "focus", function() {
        new XWiki.widgets.Suggest(this, {
             script: '$xwiki.getURL("${doc.space}.WebHome", "view")?xpage=suggest&amp;classname=ReleaseNotes.Code.ReleaseNoteClass&amp;fieldname=product&amp;secCol=-&amp;',
             varname: "input", 
             seps: " ,|",
             offsety: 13
        });
      });
    }
  }); // end of doc observe
})();
